head.script:
scwin.onpageload = function () {
    scwin.onresize();
    scwin.code = "";
    scwin.time = 0;
};

scwin.onresize = function (e) {
    // resize 이벤트 정의
    window.onresize = function () {
        scwin.applyResponsiveWidth();
    };

    // 페이지 로딩 시에도 한 번 실행
    scwin.applyResponsiveWidth();
};

//반응형 width 값 조절
scwin.applyResponsiveWidth = function () {
    let width = window.innerWidth;

    if (width <= 650) {
        signup_form.setStyle("width", "100%");
        signup_group.setStyle("width", "80%");
        findPwd_button.setStyle("width", "80%");
    } else if (width <= 1300) {
        signup_form.setStyle("width", "70%");
        signup_group.setStyle("width", "75%");
        findPwd_button.setStyle("width", "75%");
    } else {
        signup_form.setStyle("width", "40%");
        signup_group.setStyle("width", "60%");
        findPwd_button.setStyle("width", "60%");
    }
};

//input border 스타일
scwin.input_onblur = function (e) {
    const currentInputId = e.target.id;
    const targetGroupId = currentInputId.replace("_input", "_group");
    const targetGroup = $p.getComponentById(targetGroupId);

    if (targetGroup) {
        targetGroup.setStyle("border-color", "#DAD3D3");
    }
};

//input border 스타일
scwin.input_onfocus = function (e) {
    const currentInput = e.target;
    const currentInputId = currentInput.id;
    const targetGroupId = currentInputId.replace("_input", "_group");
    const targetGroup = $p.getComponentById(targetGroupId);

    if (targetGroup) {
        targetGroup.setStyle("border-color", "black");
    }
};

//메일전송 or 확인 버튼
scwin.findPwd_button_onclick = function (e) {
    if (findPwd_button.getValue() === "메일전송") {
        let param = {
            userId: email_input.getValue(),
            userName: "",
            password: "",
            profileImageUrl: "",
            isActive: "",
            createdAt: ""
        }
        dma_emailCheck.setJSON(param);

        $c.sbm.execute(sbm_checkEmail); //이메일 존재 여부 확인

    } else {
        if (code_input.getValue() === scwin.code) {
            sessionStorage.setItem("email", email_input.getValue()); //인증코드
            $p.url("/InsWebApp/websquare/websquare.html?w2xPath=/InsWebApp/ui/auth/changePwdPage.xml");
        } else {
            $c.win.alert("인증 코드가 일치하지 않습니다.");
        }
    }
};


scwin.sbm_checkEmail_submitdone = function (e) {
    const responseData = e.responseJSON;

    if (responseData.isDup) {
        code_box.addClass("show"); //인증코드 input 나타나게
        findPwd_button.setValue("확인"); //버튼 text를 메일전송 -> 확인
        $c.sbm.execute(sbm_email); //인증코드 이메일 보내기
        scwin.time = new Date().getTime();
        scwin.startCountdown(); //시간 측정
        email_error_text.setValue("");
    } else {
        email_error_text.setValue("존재하지 않는 이메일 입니다");
    }
};

scwin.sbm_email_submitdone = function (e) {
    const responseData = e.responseJSON;
    scwin.code = responseData.code;
};


//인증시간 만료 측정(10분)
scwin.startCountdown = function () {
    const timer = setInterval(function () {
        const now = new Date().getTime();
        const remaining = 600000 - (now - scwin.time); // 10분 = 600,000ms

        if (remaining <= 0) {
            timeCheck_text.setValue("인증 시간이 만료되었습니다.");
            clearInterval(timer);
            return;
        }

        const min = Math.floor(remaining / (60 * 1000));
        const sec = Math.floor((remaining % (60 * 1000)) / 1000);
        timeCheck_text.setValue(`남은 시간: ${min}분 ${sec < 10 ? "0" : ""}${sec}초`);
    }, 1000);
};
